import ceph from 'ceph'

let conn

const configDriver = ({ options }) => {
  conn = new ceph.Connection({
    endPoint: options.baseURL,
    accessKey: options.accessKey,
    secretAccessKey: options.secretAccessKey,
    bucket: options.bucket
  })
}

const getBucket = () => {
  return conn
}

export {
  configDriver,
  getBucket
}
