import nodemailer from 'nodemailer'
import { logger } from 'papagaio'

let transporter
const configDriver = ({ options }) => {
  try {
    transporter = nodemailer.createTransport({
      host: options.host,
      port: options.port,
      secure: options.secure
    })
  } catch (err) {
    logger(['porg', 'err', 'email'], `Issues configurating email `)
    throw new Error(err)
  }
}

