
import { setupErrorInternal, setupError } from '@/plugins/errors'

export default () => {
  setupErrorInternal({ key: 'internal-error' })
  setupErrorInternal({ key: 'errors-error' })

  setupError({
    key: 'validation-error',
    statusCode: 400,
    translations: {
      en: 'Invalid format for data submited',
      pt: 'Formato invalido para data submetida'
    }
  })
  setupError({
    key: '_route-not-found',
    statusCode: 404,
    translations: {
      en: 'Invalid endpoint',
      pt: 'Esta rota é inválida'
    }
  })
}
