import StatsHealthcheckService from '@/plugins/healthcheck/services/StatsHealthcheckService'
export default (options) => {
  const handler = async (request, h) => {
    return StatsHealthcheckService(options)
  }

  const config = {
    description: 'Check status for connected drivers',
    plugins: {
      'porg-auth': {
        type: 'user-session',
        roles: ['admin']
      }
    }
  }
  return { handler, config }
}
