import StatusHealthcheckService from '@/plugins/healthcheck/services/StatusHealthcheckService'
export default (options) => {
  const handler = async (request, h) => {
    return StatusHealthcheckService(options)
  }

  const config = {
    description: 'Check status for connected drivers',
    plugins: {
      'porg-auth': {
        type: 'no-auth'
      }
    }
  }
  return { handler, config }
}
