import { getBucket } from '@/drivers/Ceph'

export default class CephStorageProvider {
  constructor ({ rootPath }) {
    this.rootPath = rootPath
  }

  async storeFile ({ file, path }) {
    return getBucket().createObject(path, file)
  }

  async downloadFile ({ filePath }) {
    return getBucket().readObject(filePath)
  }

  async deleteFile ({ filePath }) {
    return getBucket().deleteObject(filePath)
  }

  async listFiles ({ searchPath }) {
    throw Error('Operation not supported')
  }
}
